  robots: robots,
  searchfield: "",
};

const App = () => {
  return (
    <div className="tc">
      <h1 className="tc">RoboFriends</h1>
      {/*
      SearchBox and CardList need to communicate in order to complete the searchBox
      We need to use "STATE" 
      Parent is STATE & Child component is PROPS
      */}
      <SearchBox />
      <CardList robots={robots} />
    </div>
  );
};

export default App;
